package com.styntax.class8J;

public class ShorthandOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=10;
		i=i+9;
		System.out.println(i);// 19
		int a=10;
		a+=9;
		System.out.println();//19
		a-=5;   //14
		a*=2;
		a/=4;
		a%=3;
		
		System.out.println(a);
		

	}

}
