package com.syntax.class1;

public class PrimitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// storing and representing numeric values;
		byte box1 =127;
		short box2=(short) 32767;
		
		int box3=120000; //popular
		long box4=1000000000L; // credit card 
				
		
		
		// Storing and representing decimals
		float variable1=1.99F;
		
		double variable2=99.99;//popular
		
		
		//storing single characters
		
		char container = 'a';
		char dollar='$';
		
		//storing yes or no values
		boolean hot=true;
		boolean tired=false;
		
		System.out.println(variable2 );
		
		System.out.println("variable2");
		
		System.out.println(tired);  //upper or lower case matter be careful
		System.out.println(hot);
		System.out.println(dollar);
		System.out.println(container);
		System.out.println(variable1);
		System.out.println(variable2);
		
		
		
		
		
		

	}

}
