package com.syntax.class9JLoops;

public class ContunieLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	for(int i=1; i<5; i++)
	
	
	
	{
		if (i==3) {continue;
	}
	
	
	System.out.println("Hello");
	System.out.println("How are you");
	System.out.println(i);
	
	}
	
	
	
	
	
	
	}
	
	
	

}
